import React, { useState } from 'react';
import axios from 'axios';

const MapViewer = ({ setLoading }) => {
  const [city, setCity] = useState('');
  const [info, setInfo] = useState(null);

  const fetchCityInfo = async () => {
    setLoading(true);
    try {
      const res = await axios.get(`http://localhost:5001/api/wiki/${city}`);
      setInfo(res.data.extract);
    } catch (err) {
      setInfo('City not found or API error');
    }
    setLoading(false);
  };

  return (
    <div className="p-4 dark:text-white">
      <input
        className="border px-3 py-2 rounded mr-2"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        placeholder="Enter city name"
      />
      <button className="bg-blue-600 text-white px-4 py-2 rounded" onClick={fetchCityInfo}>
        Explore
      </button>
      {info && <p className="mt-4">{info}</p>}
    </div>
  );
};

export default MapViewer;
