import React, { useRef, useState } from "react";

const TextToSpeech = ({ text }) => {
  const synth = window.speechSynthesis;
  const utteranceRef = useRef(null);
  const [isSpeaking, setIsSpeaking] = useState(false);

  const speak = () => {
    if (!text || synth.speaking) return;
    const utter = new SpeechSynthesisUtterance(text);
    utteranceRef.current = utter;
    synth.speak(utter);
    setIsSpeaking(true);

    utter.onend = () => setIsSpeaking(false);
  };

  const pause = () => synth.pause();
  const resume = () => synth.resume();
  const stop = () => {
    synth.cancel();
    setIsSpeaking(false);
  };

  return (
    <div className="flex gap-2 mt-4">
      <button onClick={speak} className="bg-green-600 px-3 py-1 rounded text-white">üîä Play</button>
      <button onClick={pause} className="bg-yellow-600 px-3 py-1 rounded text-white">‚è∏ Pause</button>
      <button onClick={resume} className="bg-blue-600 px-3 py-1 rounded text-white">‚ñ∂ Resume</button>
      <button onClick={stop} className="bg-red-600 px-3 py-1 rounded text-white">‚èπ Stop</button>
    </div>
  );
};

export default TextToSpeech;
