import React, { useState } from "react";
import axios from "axios";
import "./index.css";

function App() {
  const [city, setCity] = useState("");
  const [info, setInfo] = useState(null);
  const [error, setError] = useState("");

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:5001/api/wiki/${city}`);
      setInfo(response.data);
      setError("");
    } catch (err) {
      setError("City not found or API error");
      setInfo(null);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-500 to-blue-500 flex flex-col items-center justify-center text-white px-4">
      <h1 className="text-4xl font-bold mb-6">üåç ReLive: Explore Cities</h1>
      <div className="flex gap-2 mb-6">
        <input
          className="p-2 rounded text-black"
          type="text"
          placeholder="Enter city name..."
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <button
          onClick={handleSearch}
          className="bg-white text-purple-600 font-bold px-4 py-2 rounded hover:bg-purple-100"
        >
          Search
        </button>
      </div>
      {error && <p className="text-red-200">{error}</p>}
      {info && (
        <div className="bg-white/10 backdrop-blur p-6 rounded-lg max-w-xl text-left">
          <h2 className="text-2xl font-semibold mb-2">{info.title}</h2>
          <p>{info.extract}</p>
          {info.thumbnail && (
            <img
              className="mt-4 rounded"
              src={info.thumbnail.source}
              alt={info.title}
            />
          )}
        </div>
      )}
    </div>
  );
}

export default App;
