import React, { useState } from "react";
import axios from "axios";
import CityCard from "./components/CityCard";
import Loader from "./components/Loader";
import ThemeToggle from "./components/ThemeToggle";
import VoiceAssistant from "./components/VoiceAssistant";
import LanguageSelector from "./components/LanguageSelector";
import { speak } from "./utils/speak";
import { translateText } from "./utils/translate";
import "./styles/index.css";

function App() {
  const [city, setCity] = useState("");
  const [info, setInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(true);
  const [language, setLanguage] = useState("en");
  const [error, setError] = useState("");

  const fetchCity = async () => {
    if (!city.trim()) return;
    setLoading(true);
    setError("");
    setInfo(null);
    try {
      const res = await axios.get(`http://localhost:5001/api/wiki/${city}`);
      let data = res.data;

      if (language !== "en") {
        data.extract = await translateText(data.extract, language);
      }

      setInfo(data);
      speak(data.extract, language);
    } catch {
      setError("City not found or API error");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={darkMode ? "dark" : ""}>
      <div className="min-h-screen px-6 py-10 bg-white dark:bg-gray-900 text-gray-900 dark:text-white transition">
        <div className="max-w-4xl mx-auto space-y-8">
          <header className="flex items-center justify-between">
            <h1 className="text-4xl font-bold">ReLive</h1>
            <ThemeToggle darkMode={darkMode} setDarkMode={setDarkMode} />
          </header>

          <div className="flex flex-col sm:flex-row gap-4">
            <input
              value={city}
              onChange={(e) => setCity(e.target.value)}
              placeholder="Enter a city..."
              className="flex-grow px-4 py-3 rounded-lg border dark:border-gray-600 focus:outline-none"
            />
            <button
              onClick={fetchCity}
              className="bg-indigo-600 hover:bg-indigo-700 text-white px-6 py-3 rounded-lg"
            >
              üîç Search
            </button>
          </div>

          <LanguageSelector setLanguage={setLanguage} />
          <VoiceAssistant setCity={setCity} onCommand={fetchCity} />

          {loading && <Loader />}
          {error && <p className="text-red-500 text-center">{error}</p>}
          {info && <CityCard data={info} />}
        </div>
      </div>
    </div>
  );
}

export default App;
