import React, { useState } from "react";
import axios from "axios";
import CityCard from "./components/CityCard";
import Loader from "./components/Loader";
import VoiceAssistant from "./components/VoiceAssistant";

function App() {
  const [city, setCity] = useState("");
  const [info, setInfo] = useState(null);
  const [mapData, setMapData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(true);
  const [error, setError] = useState("");

  const toggleDark = () => setDarkMode(!darkMode);

  const fetchCity = async (inputCity = city) => {
    const cityToFetch = inputCity || city;
    if (!cityToFetch.trim()) return;

    setLoading(true);
    setError("");
    setInfo(null);
    setMapData(null);

    try {
      const res = await axios.get(`http://localhost:5001/api/wiki/${cityToFetch}`);
      setInfo(res.data);

      const geoRes = await axios.get(`http://localhost:5001/api/coordinates/${cityToFetch}`);
      setMapData(geoRes.data);
    } catch {
      setError("City not found or API error");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={darkMode ? "dark" : ""}>
      <div className="min-h-screen p-6 transition-colors duration-500 dark:bg-gray-900 dark:text-white">
        <header className="flex items-center justify-between max-w-3xl mx-auto">
          <h1 className="text-4xl font-semibold text-gray-800 dark:text-gray-100">ReLive</h1>
          <button
            onClick={toggleDark}
            className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-full shadow-lg transition"
          >
            {darkMode ? "‚òÄ Light Mode" : "üåô Dark Mode"}
          </button>
        </header>

        <div className="flex flex-col md:flex-row items-center gap-4 mt-10 max-w-3xl mx-auto">
          <input
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder="Enter city name (e.g. Paris)"
            className="flex-grow px-4 py-3 rounded-lg border border-gray-300 dark:border-gray-600 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition dark:bg-gray-800 dark:text-white"
          />
          <button
            onClick={() => fetchCity()}
            className="px-6 py-3 bg-indigo-600 hover:bg-indigo-700 text-white font-medium rounded-lg shadow transition"
          >
            üîç Search
          </button>
        </div>

        <VoiceAssistant onResult={(speechText) => {
          setCity(speechText);
          fetchCity(speechText);
        }} />

        {loading && <Loader />}

        {error && (
          <p className="text-red-600 mt-6 text-center max-w-3xl mx-auto">{error}</p>
        )}

        {info && <CityCard data={info} mapData={mapData} />}
      </div>
    </div>
  );
}

export default App;
