import React, { useState } from "react";
import axios from "axios";
import CityCard from "./components/CityCard";
import Loader from "./components/Loader";
import VoiceAssistant from "./components/VoiceAssistant";

function App() {
  const [city, setCity] = useState("");
  const [info, setInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(true);
  const [error, setError] = useState("");

  const toggleDark = () => setDarkMode(!darkMode);

  const fetchCity = async () => {
    if (!city.trim()) return;
    setLoading(true);
    setError("");
    setInfo(null);
    try {
      const res = await axios.get(`http://localhost:5001/api/wiki/${city}`);
      if (res.data?.title === "Not found." || res.data?.extract === undefined) {
        throw new Error("Invalid city");
      }
      setInfo(res.data);
    } catch (err) {
      setError("City not found or API error");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={darkMode ? "dark" : ""}>
      <div className="min-h-screen p-6 transition duration-500 dark:bg-gray-900 dark:text-white">
        <header className="flex items-center justify-between max-w-3xl mx-auto">
          <h1 className="text-4xl font-bold">ReLive</h1>
          <button
            onClick={toggleDark}
            className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-full transition"
          >
            {darkMode ? "‚òÄ Light" : "üåô Dark"}
          </button>
        </header>

        <div className="flex flex-col md:flex-row items-center gap-4 mt-10 max-w-3xl mx-auto">
          <input
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder="Enter city name (e.g. London)"
            className="flex-grow px-4 py-3 rounded-lg border border-gray-300 dark:border-gray-700 dark:bg-gray-800 dark:text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
          />
          <button
            onClick={fetchCity}
            className="px-6 py-3 bg-indigo-600 hover:bg-indigo-700 text-white font-medium rounded-lg"
          >
            üîç Search
          </button>
        </div>

        <VoiceAssistant setCity={setCity} fetchCity={fetchCity} />

        {loading && <Loader />}

        {!loading && error && (
          <p className="text-red-500 text-center mt-6">{error}</p>
        )}

        {info && <CityCard data={info} />}
      </div>
    </div>
  );
}

export default App;
