import React, { useState } from 'react';
import axios from 'axios';

const ChatGPTBox = () => {
  const [input, setInput] = useState('');
  const [reply, setReply] = useState('');

  const handleAsk = async () => {
    const res = await axios.post('http://localhost:5001/api/chat', { message: input });
    setReply(res.data.reply);
  };

  return (
    <div className="p-4 dark:text-white">
      <h2 className="text-lg mb-2">ðŸ’¬ Ask ChatGPT</h2>
      <input
        className="border px-3 py-2 rounded mr-2"
        placeholder="Ask something..."
        value={input}
        onChange={(e) => setInput(e.target.value)}
      />
      <button onClick={handleAsk} className="bg-green-600 text-white px-4 py-2 rounded">
        Ask
      </button>
      {reply && <p className="mt-2 italic">{reply}</p>}
    </div>
  );
};

export default ChatGPTBox;
