import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import CityCard from "./components/CityCard";
import Loader from "./components/Loader";
import Map from "./components/Map"; // component that shows the map
import ThemeToggle from "./components/ThemeToggle";

function App() {
  const [city, setCity] = useState("");
  const [info, setInfo] = useState(null);
  const [coords, setCoords] = useState(null);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(true);
  const [error, setError] = useState("");

  const recognitionRef = useRef(null);

  const toggleDark = () => setDarkMode((prev) => !prev);

  // Fetch city info and coordinates
  const fetchCity = async (cityInput) => {
    const trimmedCity = cityInput.trim();
    if (!trimmedCity) return;
    setLoading(true);
    setError("");
    setInfo(null);
    setCoords(null);
    try {
      const res = await axios.get(
        `http://localhost:5001/api/wiki/${encodeURIComponent(trimmedCity)}`
      );

      // Wikipedia sometimes returns error as valid JSON
      if (!res.data?.extract || res.data?.type === "https://mediawiki.org/wiki/HyperSwitch/errors/not_found") {
        setError("City not found or unavailable.");
        return;
      }

      setInfo(res.data);

      // Get coordinates for map
      const geo = await axios.get(
        `http://localhost:5001/api/coordinates/${encodeURIComponent(trimmedCity)}`
      );

      if (geo.data?.lat && geo.data?.lng) {
        setCoords(geo.data);
      }
    } catch (err) {
      console.error("Fetch error:", err);
      setError("City not found or API error");
    } finally {
      setLoading(false);
    }
  };

  // Handle voice recognition
  const startListening = () => {
    if (!("webkitSpeechRecognition" in window)) {
      alert("Your browser does not support speech recognition.");
      return;
    }

    const recognition = new window.webkitSpeechRecognition();
    recognition.lang = "en-US";
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setCity(transcript);
      fetchCity(transcript); // Auto-fetch on result
    };

    recognition.onerror = (e) => {
      console.error("Speech recognition error:", e);
      setError("Voice input failed. Please try again.");
    };

    recognition.start();
    recognitionRef.current = recognition;
  };

  return (
    <div className={darkMode ? "dark" : ""}>
      <div className="min-h-screen p-6 bg-gray-100 dark:bg-gray-900 transition-colors duration-500">
        <header className="flex items-center justify-between max-w-3xl mx-auto">
          <h1 className="text-4xl font-bold text-gray-900 dark:text-white">ReLive</h1>
          <ThemeToggle darkMode={darkMode} toggleDark={toggleDark} />
        </header>

        <div className="flex flex-col md:flex-row items-center gap-4 mt-10 max-w-3xl mx-auto">
          <input
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder="Enter city name"
            className="flex-grow px-4 py-3 rounded-lg border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
          />
          <button
            onClick={() => fetchCity(city)}
            className="px-6 py-3 bg-indigo-600 hover:bg-indigo-700 text-white font-medium rounded-lg shadow transition"
          >
            Search
          </button>
          <button
            onClick={startListening}
            className="px-6 py-3 bg-green-600 hover:bg-green-700 text-white font-medium rounded-lg shadow transition"
          >
            🎙️ Speak
          </button>
        </div>

        {loading && <Loader />}

        {error && (
          <p className="text-red-600 mt-6 text-center max-w-3xl mx-auto">{error}</p>
        )}

        {info && (
          <div className="max-w-3xl mx-auto mt-8 space-y-6">
            <CityCard data={info} />
            {coords && <Map lat={coords.lat} lng={coords.lng} />}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
