import React, { useState } from "react";
import axios from "axios";
import CityCard from "./components/CityCard";
import Loader from "./components/Loader";

function App() {
  const [city, setCity] = useState("");
  const [info, setInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(true);
  const [error, setError] = useState("");

  const toggleDark = () => setDarkMode(!darkMode);

  const fetchCity = async () => {
    if (!city.trim()) return;
    setLoading(true);
    setError("");
    setInfo(null);
    try {
      const res = await axios.get(`http://localhost:5001/api/wiki/${city}`);
      setInfo(res.data);
    } catch {
      setError("City not found or API error");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={darkMode ? "dark" : ""}>
      <div className="min-h-screen px-6 py-8 transition-all duration-700 ease-in-out bg-gradient-to-br from-indigo-100 to-purple-200 dark:from-gray-900 dark:to-black text-gray-900 dark:text-white">
        {/* Header */}
        <header className="flex items-center justify-between max-w-5xl mx-auto">
          <h1 className="text-4xl md:text-5xl font-extrabold tracking-tight text-gray-800 dark:text-white">
            üåç ReLive
          </h1>
          <button
            onClick={toggleDark}
            className="bg-gray-900 dark:bg-white dark:text-gray-800 text-white px-4 py-2 rounded-full shadow-lg transition-all hover:scale-105"
          >
            {darkMode ? "‚òÄ Light Mode" : "üåô Dark Mode"}
          </button>
        </header>

        {/* Search Bar */}
        <div className="flex flex-col md:flex-row items-center gap-4 mt-12 max-w-4xl mx-auto">
          <input
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder="Enter city name (e.g. Paris)"
            className="w-full md:flex-1 px-5 py-3 rounded-lg border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-gray-900 dark:text-white placeholder-gray-500 dark:placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition"
          />
          <button
            onClick={fetchCity}
            className="px-6 py-3 bg-indigo-600 hover:bg-indigo-700 text-white font-medium rounded-lg shadow-md transition hover:scale-105"
          >
            üîç Search
          </button>
        </div>

        {/* Loader */}
        {loading && <Loader />}

        {/* Error */}
        {error && (
          <p className="text-red-500 mt-6 text-center font-semibold max-w-3xl mx-auto">
            {error}
          </p>
        )}

        {/* Result */}
        {info && (
          <div className="mt-8">
            <CityCard data={info} />
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
