import React, { useState, useRef, useEffect } from "react";
import { Loader } from "@googlemaps/js-api-loader";

export default function App() {
  const [location, setLocation] = useState("New York");
  const [year, setYear] = useState(2011);
  const [summary, setSummary] = useState(""); // üÜï For Wikipedia summary
  const mapRef = useRef(null);
  const panoramaRef = useRef(null);

  // üß† Wikipedia Fetcher
  useEffect(() => {
    fetch(`http://localhost:5000/api/wiki/${location}`)
      .then((res) => res.json())
      .then((data) => {
        setSummary(data.summary || "No summary available.");
      })
      .catch(() => setSummary("Failed to fetch Wikipedia data."));
  }, [location]);

  useEffect(() => {
    const loader = new Loader({
      apiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
      version: "weekly",
    });

    loader.load().then(() => {
      const geocoder = new window.google.maps.Geocoder();
      geocoder.geocode({ address: location }, (results, status) => {
        if (status === "OK" && results[0]) {
          const loc = results[0].geometry.location;

          const map = new window.google.maps.Map(mapRef.current, {
            center: loc,
            zoom: 14,
          });

          const panorama = new window.google.maps.StreetViewPanorama(
            panoramaRef.current,
            {
              position: loc,
              pov: { heading: 160, pitch: 0 },
              zoom: 1,
            }
          );

          const sv = new window.google.maps.StreetViewService();
          sv.getPanorama({ location: loc, radius: 50 }, (data, status) => {
            if (status === "OK") {
              panorama.setPano(data.location.pano);
            }
          });

          map.setStreetView(panorama);
        }
      });
    });
  }, [location, year]);

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <h1 className="text-3xl font-bold mb-6">üï∞Ô∏è ReLive: Travel Through Time</h1>
      <div className="flex gap-4 mb-4 items-center">
        <input
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          placeholder="Enter a location (e.g., Paris)"
          className="text-black p-2 rounded"
        />
        <input
          type="range"
          min="2007"
          max="2023"
          value={year}
          onChange={(e) => setYear(Number(e.target.value))}
          className="w-48"
        />
        <span className="text-lg">{year}</span>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-4">
        <div ref={mapRef} className="w-full h-[300px] md:h-[400px] rounded shadow-lg" />
        <div ref={panoramaRef} className="w-full h-[300px] md:h-[400px] rounded shadow-lg" />
      </div>

      {/* üìù Wikipedia Summary */}
      <div className="mt-4 bg-gray-800 p-4 rounded shadow">
        <h2 className="text-xl font-semibold mb-2">üìö Wikipedia Summary:</h2>
        <p>{summary}</p>
      </div>
    </div>
  );
}
