import React from "react";

const CityCard = ({ data }) => (
  <div className="max-w-3xl mx-auto mt-10 p-8 bg-white bg-opacity-60 backdrop-blur-md dark:bg-gray-800 dark:bg-opacity-60 rounded-2xl shadow-2xl transition">
    <h2 className="text-3xl font-bold text-gray-800 dark:text-gray-100">{data.title}</h2>
    <p className="mt-4 text-gray-700 dark:text-gray-300">{data.extract}</p>
    {data.thumbnail?.source && (
      <img
        src={data.thumbnail.source}
        alt={data.title}
        className="mt-6 rounded-lg shadow-lg w-full max-h-96 object-cover"
      />
    )}
    {data.content_urls?.desktop?.page && (
      <a
        href={data.content_urls.desktop.page}
        target="_blank"
        rel="noopener noreferrer"
        className="inline-block mt-4 text-indigo-600 dark:text-indigo-400 font-semibold hover:underline"
      >
        Read more on Wikipedia â†’
      </a>
    )}
  </div>
);

export default CityCard;
