import React from "react";

const CityInfoCard = ({ data }) => {
  if (!data) return null;

  return (
    <div style={styles.card}>
      <h2>{data.title}</h2>
      {data.thumbnail && (
        <img src={data.thumbnail.source} alt={data.title} style={styles.image} />
      )}
      <p>{data.description}</p>
      <a href={data.content_urls.desktop.page} target="_blank" rel="noopener noreferrer">
        Read more on Wikipedia
      </a>
    </div>
  );
};

const styles = {
  card: {
    padding: "20px",
    maxWidth: "600px",
    margin: "auto",
    backgroundColor: "#f9f9f9",
    borderRadius: "8px",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)"
  },
  image: {
    width: "100%",
    maxHeight: "300px",
    objectFit: "cover",
    borderRadius: "6px"
  }
};

export default CityInfoCard;
