import React, { useState } from "react";
import axios from "axios";
import CityCard from "./components/CityCard";
import Loader from "./components/Loader";
import Map from "./components/Map";
import VoiceAssistant from "./components/VoiceAssistant";

function App() {
  const [city1, setCity1] = useState("");
  const [city2, setCity2] = useState("");
  const [info1, setInfo1] = useState(null);
  const [info2, setInfo2] = useState(null);
  const [coordinates1, setCoordinates1] = useState(null);
  const [coordinates2, setCoordinates2] = useState(null);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(true);
  const [error1, setError1] = useState("");
  const [error2, setError2] = useState("");
  const [volume, setVolume] = useState(1); // volume slider (0 to 1)

  const toggleDark = () => setDarkMode(!darkMode);

  const speak = (text) => {
    if (text && window.speechSynthesis) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.volume = volume;
      utterance.rate = 1;
      utterance.pitch = 1;
      window.speechSynthesis.cancel(); // stop previous speech
      window.speechSynthesis.speak(utterance);
    }
  };

  const fetchCity = async (city, setInfo, setCoordinates, setError) => {
    if (!city.trim()) return;
    setLoading(true);
    setError("");
    setInfo(null);
    setCoordinates(null);

    try {
      const res = await axios.get(`http://localhost:5001/api/wiki/${city}`);
      setInfo(res.data);
    } catch (err) {
      setError("City not found in Wikipedia");
      setLoading(false);
      return;
    }

    try {
      const coordRes = await axios.get(`http://localhost:5001/api/coordinates/${city}`);
      setCoordinates(coordRes.data);
    } catch (err) {
      console.warn("Map/Coordinates not found.");
      setCoordinates(null);
    } finally {
      setLoading(false);
    }
  };

  const handleVoiceResult1 = (spokenText) => {
    setCity1(spokenText);
    setTimeout(() => fetchCity(spokenText, setInfo1, setCoordinates1, setError1), 500);
  };

  const handleVoiceResult2 = (spokenText) => {
    setCity2(spokenText);
    setTimeout(() => fetchCity(spokenText, setInfo2, setCoordinates2, setError2), 500);
  };

  return (
    <div className={darkMode ? "dark" : ""}>
      <div className="min-h-screen p-6 transition-colors duration-500 dark:bg-gray-900 dark:text-white">
        <header className="flex items-center justify-between max-w-6xl mx-auto">
          <h1 className="text-4xl font-semibold">ReLive</h1>
          <button
            onClick={toggleDark}
            className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-full shadow-lg transition"
          >
            {darkMode ? "Light Mode" : "Dark Mode"}
          </button>
        </header>

        {/* Dual City Inputs */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mt-10 max-w-6xl mx-auto">
          {[1, 2].map((num) => {
            const city = num === 1 ? city1 : city2;
            const setCity = num === 1 ? setCity1 : setCity2;
            const error = num === 1 ? error1 : error2;
            const info = num === 1 ? info1 : info2;
            const coordinates = num === 1 ? coordinates1 : coordinates2;
            const setInfo = num === 1 ? setInfo1 : setInfo2;
            const setCoordinates = num === 1 ? setCoordinates1 : setCoordinates2;
            const setError = num === 1 ? setError1 : setError2;
            const voiceHandler = num === 1 ? handleVoiceResult1 : handleVoiceResult2;

            return (
              <div key={num}>
                {/* Input + Voice Assistant */}
                <div className="flex gap-2 mb-4">
                  <input
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    placeholder={`Enter city ${num}`}
                    className="flex-grow px-4 py-3 rounded-lg border border-gray-300 dark:border-gray-600 dark:bg-gray-800 dark:text-white focus:outline-none focus:ring-2 focus:ring-indigo-500 transition"
                  />
                  <button
                    onClick={() =>
                      fetchCity(city, setInfo, setCoordinates, setError)
                    }
                    className="px-4 py-3 bg-indigo-600 hover:bg-indigo-700 text-white rounded-lg shadow"
                  >
                    Search
                  </button>
                  <VoiceAssistant onResult={voiceHandler} />
                </div>

                {/* Error Message */}
                {error && (
                  <p className="text-red-600 text-sm mb-4">{error}</p>
                )}

                {/* City Info Display */}
                {info && (
                  <>
                    <CityCard data={info} />

                    {/* Text-to-Speech and Volume */}
                    <div className="flex items-center gap-4 mt-2">
                      <button
                        onClick={() => speak(info.extract)}
                        className="px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-md shadow"
                      >
                        ðŸ”Š Speak Description
                      </button>
                      <div className="flex items-center gap-2">
                        <label htmlFor={`volume${num}`} className="text-sm">
                          Volume
                        </label>
                        <input
                          id={`volume${num}`}
                          type="range"
                          min="0"
                          max="1"
                          step="0.1"
                          value={volume}
                          onChange={(e) => setVolume(parseFloat(e.target.value))}
                          className="w-24"
                        />
                      </div>
                    </div>
                  </>
                )}

                {/* Google Map */}
                {coordinates && <Map lat={coordinates.lat} lng={coordinates.lng} />}
              </div>
            );
          })}
        </div>

        {loading && <Loader />}
      </div>
    </div>
  );
}

export default App;
