import React, { useState } from "react";
import axios from "axios";

function App() {
  const [city, setCity] = useState("");
  const [info, setInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(true);
  const [error, setError] = useState(null);

  const toggleDarkMode = () => setDarkMode(!darkMode);

  const fetchInfo = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(`http://localhost:5001/api/wiki/${city}`);
      setInfo(response.data);
    } catch {
      setError("City not found or API error");
      setInfo(null);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={`${darkMode ? "bg-gray-900 text-white" : "bg-white text-gray-800"} min-h-screen transition duration-300`}>
      <div className="flex justify-between items-center p-5">
        <h1 className="text-3xl font-bold">üåç ReLive Explorer</h1>
        <button
          onClick={toggleDarkMode}
          className="bg-purple-500 hover:bg-purple-600 text-white px-4 py-2 rounded-md"
        >
          {darkMode ? "‚òÄ Light" : "üåí Dark"}
        </button>
      </div>

      <div className="flex flex-col items-center px-4">
        <input
          type="text"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          placeholder="Enter city (e.g., London)"
          className="p-3 rounded-lg border border-gray-400 w-full max-w-md mb-4 text-gray-800"
        />

        <button
          onClick={fetchInfo}
          className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg"
        >
          üîç Explore
        </button>

        {loading && <p className="mt-4">‚è≥ Loading...</p>}
        {error && <p className="text-red-500 mt-4">{error}</p>}

        {info && (
          <div className="bg-white text-gray-900 dark:bg-gray-800 dark:text-white p-6 rounded-xl shadow-xl mt-6 max-w-2xl w-full transition-all">
            <h2 className="text-2xl font-semibold mb-2">{info.title}</h2>
            {info.thumbnail?.source && (
              <img src={info.thumbnail.source} alt={info.title} className="rounded mb-4 w-full max-h-64 object-cover" />
            )}
            <p>{info.extract}</p>
            {info.content_urls?.desktop?.page && (
              <a
                href={info.content_urls.desktop.page}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block mt-4 text-blue-400 hover:underline"
              >
                Read more on Wikipedia ‚Üí
              </a>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
