import React, { useState } from "react";
import axios from "axios";

function App() {
  const [city, setCity] = useState("");
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);

  const searchCity = async () => {
    if (!city) return;
    setLoading(true);
    setData(null);
    try {
      const res = await axios.get(`http://localhost:5000/api/wiki/${city}`);
      setData(res.data);
    } catch (err) {
      console.error(err.message);
      alert("‚ùå City not found or API error.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 to-black text-white p-6 font-sans">
      <div className="max-w-2xl mx-auto text-center">
        <h1 className="text-4xl font-extrabold mb-4 text-teal-400 drop-shadow-lg">üåç ReLive</h1>
        <p className="mb-6 text-gray-300">Explore how cities have changed through time.</p>

        <div className="flex justify-center gap-2 mb-6">
          <input
            type="text"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder="Enter a city like London..."
            className="px-4 py-2 rounded-l-lg w-full max-w-md text-black focus:outline-none"
          />
          <button
            onClick={searchCity}
            className="bg-teal-500 hover:bg-teal-600 px-4 py-2 rounded-r-lg font-semibold transition"
          >
            {loading ? "Loading..." : "Search"}
          </button>
        </div>

        {data && (
          <div className="bg-gray-800 rounded-xl p-6 shadow-xl animate-fade-in">
            <h2 className="text-2xl font-bold text-teal-300 mb-2">{data.title}</h2>
            {data.thumbnail && (
              <img
                src={data.thumbnail.source}
                alt={data.title}
                className="mx-auto rounded-lg mb-4 w-64 shadow-md"
              />
            )}
            <p className="text-gray-200">{data.extract}</p>
            <a
              href={data.content_urls?.desktop?.page}
              target="_blank"
              rel="noreferrer"
              className="block mt-4 text-teal-400 underline"
            >
              Learn more on Wikipedia ‚Üí
            </a>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
