// CityCard.js
import React from "react";

const CityCard = ({ data, coords }) => {
  return (
    <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6 mt-10 max-w-3xl mx-auto text-gray-800 dark:text-gray-200">
      <h2 className="text-3xl font-bold mb-2">{data.title}</h2>
      <p className="mb-4">{data.extract}</p>

      {data.thumbnail && (
        <img
          src={data.thumbnail.source}
          alt={data.title}
          className="w-full max-h-64 object-cover rounded-lg shadow mb-4"
        />
      )}

      {coords && (
        <div className="mt-4">
          <h3 className="text-xl font-semibold mb-2">Street View</h3>
          <iframe
            width="100%"
            height="400"
            loading="lazy"
            allowFullScreen
            title="Google Maps Street View"
            className="rounded-lg"
            src={`https://www.google.com/maps/embed/v1/streetview?key=${import.meta.env.VITE_GOOGLE_MAPS_API_KEY}&location=${coords.lat},${coords.lng}&heading=210&pitch=10&fov=80`}
          ></iframe>
        </div>
      )}
    </div>
  );
};

export default CityCard;
