import React from "react";

const CityCard = ({ data, mapData }) => {
  return (
    <div className="max-w-3xl mx-auto mt-10 bg-white dark:bg-gray-800 shadow-lg rounded-lg p-6 transition">
      <h2 className="text-2xl font-bold mb-2 text-gray-800 dark:text-gray-100">{data.title}</h2>
      <p className="text-gray-700 dark:text-gray-300">{data.extract}</p>

      {mapData && (
        <div className="mt-6 space-y-6">
          <div>
            <h3 className="text-lg font-semibold mb-2">Google Maps View</h3>
            <iframe
              width="100%"
              height="300"
              style={{ border: 0 }}
              loading="lazy"
              allowFullScreen
              src={`https://www.google.com/maps/embed/v1/view?key=${import.meta.env.VITE_GOOGLE_MAPS_API_KEY}&center=${mapData.lat},${mapData.lng}&zoom=12`}
            ></iframe>
          </div>

          <div>
            <h3 className="text-lg font-semibold mb-2">Street View</h3>
            <iframe
              width="100%"
              height="300"
              style={{ border: 0 }}
              loading="lazy"
              allowFullScreen
              src={`https://www.google.com/maps/embed/v1/streetview?key=${import.meta.env.VITE_GOOGLE_MAPS_API_KEY}&location=${mapData.lat},${mapData.lng}&heading=210&pitch=10&fov=75`}
            ></iframe>
          </div>
        </div>
      )}
    </div>
  );
};

export default CityCard;
