import React, { useState, useEffect } from "react";
import axios from "axios";
import CityCard from "./components/CityCard";
import Loader from "./components/Loader";
import Map from "./components/Map";
import VoiceAssistant from "./components/VoiceAssistant";
import io from "socket.io-client";

const socket = io("http://localhost:5001");

function App() {
  const [city, setCity] = useState("");
  const [info, setInfo] = useState(null);
  const [coordinates, setCoordinates] = useState(null);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(true);
  const [error, setError] = useState("");

  const toggleDark = () => setDarkMode(!darkMode);

  const fetchCity = async (cityName) => {
    if (!cityName.trim()) return;
    setLoading(true);
    setError("");
    setInfo(null);
    setCoordinates(null);

    try {
      const res = await axios.get(`http://localhost:5001/api/wiki/${cityName}`);
      setInfo(res.data);
    } catch {
      setError("City not found in Wikipedia");
      setLoading(false);
      return;
    }

    try {
      const coordRes = await axios.get(
        `http://localhost:5001/api/coordinates/${cityName}`
      );
      setCoordinates(coordRes.data);
    } catch {
      console.warn("Coordinates not found");
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    fetchCity(city);
    socket.emit("citySearch", city); // Broadcast to others
  };

  const handleVoiceResult = (spokenText) => {
    setCity(spokenText);
    setTimeout(() => {
      fetchCity(spokenText);
      socket.emit("citySearch", spokenText);
    }, 300);
  };

  useEffect(() => {
    socket.on("cityUpdate", (incomingCity) => {
      setCity(incomingCity);
      fetchCity(incomingCity);
    });

    return () => {
      socket.off("cityUpdate");
    };
  }, []);

  return (
    <div className={darkMode ? "dark" : ""}>
      <div className="min-h-screen p-6 transition duration-500 dark:bg-gray-900 dark:text-white">
        <header className="flex items-center justify-between max-w-3xl mx-auto">
          <h1 className="text-4xl font-bold">ReLive</h1>
          <button
            onClick={toggleDark}
            className="bg-indigo-600 text-white px-4 py-2 rounded-lg shadow"
          >
            {darkMode ? "â˜€ Light Mode" : "ðŸŒ™ Dark Mode"}
          </button>
        </header>

        <div className="flex flex-col md:flex-row items-center gap-4 mt-10 max-w-3xl mx-auto">
          <input
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder="Enter city name"
            className="flex-grow px-4 py-3 rounded-lg border border-gray-300 dark:border-gray-600 dark:bg-gray-800 dark:text-white"
          />
          <button
            onClick={handleSearch}
            className="bg-indigo-600 hover:bg-indigo-700 text-white px-6 py-3 rounded-lg"
          >
            Search
          </button>
          <VoiceAssistant onResult={handleVoiceResult} />
        </div>

        {loading && <Loader />}
        {error && <p className="text-red-500 mt-4 text-center">{error}</p>}
        {info && <CityCard data={info} />}
        {coordinates && <Map lat={coordinates.lat} lng={coordinates.lng} />}
      </div>
    </div>
  );
}

export default App;
