import React, { useEffect } from "react";

function CityCard({ data }) {
  useEffect(() => {
    if (data?.extract) {
      const utterance = new SpeechSynthesisUtterance(data.extract);
      speechSynthesis.speak(utterance);
    }
  }, [data]);

  return (
    <div className="max-w-3xl mx-auto mt-10 bg-white dark:bg-gray-800 shadow-lg rounded-lg overflow-hidden">
      <img
        src={data?.thumbnail?.source || data?.originalimage?.source}
        alt={data.title}
        className="w-full h-64 object-cover"
      />
      <div className="p-6">
        <h2 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
          {data.title}
        </h2>
        <p
          className="text-gray-700 dark:text-gray-300"
          dangerouslySetInnerHTML={{ __html: data.extract_html }}
        />
        <a
          href={data.content_urls?.desktop?.page}
          target="_blank"
          rel="noopener noreferrer"
          className="inline-block mt-4 text-indigo-600 dark:text-indigo-400 underline"
        >
          Learn more on Wikipedia
        </a>
      </div>
    </div>
  );
}

export default CityCard;
