import React, { useState } from "react";
import axios from "axios";
import CityCard from "./components/CityCard";
import Loader from "./components/Loader";

function App() {
  const [city, setCity] = useState("");
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  const fetchData = async () => {
    setLoading(true);
    setData(null);
    try {
      const response = await axios.get(`http://localhost:5001/api/wiki/${city}`);
      setData(response.data);
    } catch (error) {
      setData({ title: "Error", extract: "City not found or API error." });
    }
    setLoading(false);
  };

  return (
    <div className={`${darkMode ? "dark" : ""}`}>
      <div className="min-h-screen px-4 py-8 bg-gray-100 dark:bg-gray-900">
        <div className="flex justify-between items-center">
          <h1 className="text-3xl font-bold mb-4 dark:text-white">üåç ReLive</h1>
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="px-4 py-2 bg-indigo-600 text-white rounded"
          >
            {darkMode ? "Light Mode" : "Dark Mode"}
          </button>
        </div>

        <div className="flex gap-4">
          <input
            type="text"
            placeholder="Enter city name"
            className="p-2 border rounded flex-1"
            value={city}
            onChange={(e) => setCity(e.target.value)}
          />
          <button
            onClick={fetchData}
            className="px-4 py-2 bg-blue-500 text-white rounded"
          >
            Search
          </button>
        </div>

        {loading && <Loader />}
        {data && <CityCard data={data} />}
      </div>
    </div>
  );
}

export default App;
