// client/src/App.js
import React, { useState } from "react";
import axios from "axios";
import CityCard from "./components/CityCard";
import Loader from "./components/Loader";
import VoiceAssistant from "./components/VoiceAssistant";

function App() {
  const [city, setCity] = useState("");
  const [info, setInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(true);
  const [error, setError] = useState("");
  const [map, setMap] = useState(null);

  const toggleDark = () => setDarkMode(!darkMode);

  const fetchCity = async (name = city) => {
    if (!name.trim()) return;
    setLoading(true);
    setError("");
    setInfo(null);
    try {
      const wikiRes = await axios.get(
        `http://localhost:5001/api/wiki/${encodeURIComponent(name)}`
      );
      const mapRes = await axios.get(
        `http://localhost:5001/api/coordinates/${encodeURIComponent(name)}`
      );

      setInfo(wikiRes.data);
      setMap(mapRes.data);
    } catch (err) {
      setError("City not found or API error");
    } finally {
      setLoading(false);
    }
  };

  const handleVoiceInput = (voiceText) => {
    setCity(voiceText);
    fetchCity(voiceText);
  };

  return (
    <div className={darkMode ? "dark" : ""}>
      <div className="min-h-screen bg-white dark:bg-gray-900 p-6 transition-colors duration-500">
        <header className="flex items-center justify-between max-w-3xl mx-auto">
          <h1 className="text-4xl font-semibold text-gray-800 dark:text-gray-200">
            ReLive
          </h1>
          <button
            onClick={toggleDark}
            className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-full shadow-lg transition"
          >
            {darkMode ? "‚òÄ Light Mode" : "üåô Dark Mode"}
          </button>
        </header>

        <div className="flex flex-col md:flex-row items-center gap-4 mt-10 max-w-3xl mx-auto">
          <input
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder="Enter city name (e.g. London)"
            className="flex-grow px-4 py-3 rounded-lg border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-800 text-black dark:text-white focus:outline-none focus:ring-2 focus:ring-indigo-500 transition"
          />
          <button
            onClick={() => fetchCity()}
            className="px-6 py-3 bg-indigo-600 hover:bg-indigo-700 text-white font-medium rounded-lg shadow transition"
          >
            üîç Search
          </button>
          <VoiceAssistant onResult={handleVoiceInput} />
        </div>

        {loading && <Loader />}

        {error && (
          <p className="text-red-600 mt-6 text-center max-w-3xl mx-auto">
            {error}
          </p>
        )}

        {info && (
          <div className="mt-10 max-w-3xl mx-auto bg-white dark:bg-gray-800 text-gray-800 dark:text-white rounded-lg shadow p-6">
            <CityCard data={info} />
            {map && (
              <div className="mt-6">
                <h2 className="text-lg font-semibold mb-2">Google Map View:</h2>
                <iframe
                  width="100%"
                  height="300"
                  loading="lazy"
                  allowFullScreen
                  src={`https://www.google.com/maps/embed/v1/view?key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}&center=${map.lat},${map.lng}&zoom=12`}
                  className="rounded-lg"
                ></iframe>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
