// client/src/App.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import CityCard from "./components/CityCard";
import Loader from "./components/Loader";
import VoiceAssistant from "./components/VoiceAssistant";

function App() {
  const [city, setCity] = useState("");
  const [info, setInfo] = useState(null);
  const [coords, setCoords] = useState(null);
  const [loading, setLoading] = useState(false);
  const [darkMode, setDarkMode] = useState(true);
  const [error, setError] = useState("");

  const toggleDark = () => setDarkMode(!darkMode);

  const fetchCity = async () => {
    if (!city.trim()) return;
    setLoading(true);
    setError("");
    setInfo(null);
    setCoords(null);

    try {
      const [wikiRes, coordRes] = await Promise.all([
        axios.get(`http://localhost:5001/api/wiki/${city}`),
        axios.get(`http://localhost:5001/api/coordinates/${city}`),
      ]);

      const wikiData = wikiRes.data;
      const location = coordRes.data;

      if (!wikiData || !location) {
        setError("City info or map not found");
      } else {
        setInfo(wikiData);
        setCoords(location);
      }
    } catch (err) {
      setError("City not found or API error");
    } finally {
      setLoading(false);
    }
  };

  const handleVoiceInput = (voiceText) => {
    setCity(voiceText);
    setTimeout(() => fetchCity(), 500); // Fetch after setting
  };

  return (
    <div className={darkMode ? "dark" : ""}>
      <div className="min-h-screen p-6 bg-white dark:bg-gray-900 transition duration-300">
        <header className="flex items-center justify-between max-w-3xl mx-auto">
          <h1 className="text-4xl font-semibold text-gray-800 dark:text-gray-200">ReLive</h1>
          <button
            onClick={toggleDark}
            className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-full shadow transition"
          >
            {darkMode ? "â˜€ Light" : "ðŸŒ™ Dark"}
          </button>
        </header>

        <div className="flex flex-col md:flex-row items-center gap-4 mt-10 max-w-3xl mx-auto">
          <input
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder="Enter city name"
            className="flex-grow px-4 py-3 rounded-lg border border-gray-300 dark:border-gray-700 text-black dark:text-white bg-white dark:bg-gray-800 placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-indigo-500"
          />
          <button
            onClick={fetchCity}
            className="px-6 py-3 bg-indigo-600 hover:bg-indigo-700 text-white font-medium rounded-lg shadow transition"
          >
            Search
          </button>
          <VoiceAssistant onVoiceResult={handleVoiceInput} />
        </div>

        {loading && <Loader />}

        {error && !info && !coords && (
          <p className="text-red-500 mt-6 text-center">{error}</p>
        )}

        {info && <CityCard data={info} coords={coords} />}
      </div>
    </div>
  );
}

export default App;
